    private void calcButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcButtonActionPerformed
        String ausgabe = "NULL";
        Manager manager = new Manager();
        BitMatrix2D matrix = new BitMatrix2D(this.generatormatrix.getText());

        if (this.codewoerter.isSelected())
          ausgabe = manager.codewoerter(matrix);

        if (this.syndrome.isSelected())
            ausgabe = manager.syndrome(matrix);

        if (this.korregieren.isSelected());

        this.ausgabe.setText(ausgabe);
    }//GEN-LAST:event_calcButtonActionPerformed

